    if (alpha < alphaThreshold) discard;
  #endif
}
    uniform vec4 startColor;
    uniform vec4 endColor;
    uniform vec2 startPosition;
    uniform vec2 endPosition;
varying vec4 color;
varying vec2 uv0;
uniform sampler2D cc_spriteTexture;
   uniform float colorRadio;
vec4 calculateGradient(vec4 baseColor) {
  vec2 pixelPos = uv0 * 2.0 - 1.0;
  vec2 gradientDir = normalize(endPosition.xy - startPosition.xy);
  float t = dot(pixelPos - startPosition.xy, gradientDir) / length(endPosition.xy - startPosition.xy);
  t = clamp(t, 0.0, 1.0);
  vec4 gradientColor = mix(startColor, endColor, smoothstep(0.0, colorRadio * 2.0, t));
  return gradientColor;
}
vec4 premultipliedAlpha() {
  vec4 o = CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
  vec4 gradient = calculateGradient(o);
  o *= gradient;
  o *= color.a;
  ALPHA_TEST(o);
  return o;
}
void main() { gl_FragColor = premultipliedAlpha(); }
